#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\G\posmul\node_modules\.pnpm\ts-jest@29.4.0_@babel+core@_98d71c7ce37704cf464a2fb09fbadcd7\node_modules\ts-jest\node_modules;C:\G\posmul\node_modules\.pnpm\ts-jest@29.4.0_@babel+core@_98d71c7ce37704cf464a2fb09fbadcd7\node_modules;C:\G\posmul\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/G/posmul/node_modules/.pnpm/ts-jest@29.4.0_@babel+core@_98d71c7ce37704cf464a2fb09fbadcd7/node_modules/ts-jest/node_modules:/mnt/c/G/posmul/node_modules/.pnpm/ts-jest@29.4.0_@babel+core@_98d71c7ce37704cf464a2fb09fbadcd7/node_modules:/mnt/c/G/posmul/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../cli.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../cli.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../cli.js" $args
  } else {
    & "node$exe"  "$basedir/../../cli.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
